package com.example.carlosjr.simplemvckotlin.configimport com.github.benmanes.caffeine.cache.CaffeineSpecimport org.springframework.cache.CacheManagerimport org.springframework.cache.annotation.EnableCachingimport org.springframework.cache.caffeine.CaffeineCacheManagerimport org.springframework.context.annotation.Beanimport org.springframework.context.annotation.Configuration@EnableCaching@Configurationclass CacheConfig {    @Bean    fun cacheManager(): CacheManager? {        val cacheManager = CaffeineCacheManager("person-cache")        cacheManager.setCaffeineSpec(CaffeineSpec.parse("maximumSize=500,expireAfterAccess=60s"))        return cacheManager    }}